#pragma config(Motor,  port2,           mLift,         tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port4,           mLeft,         tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port6,           mRight,        tmotorVex393_MC29, openLoop, reversed)
#pragma config(Motor,  port10,          mBucket,       tmotorVex393_HBridge, openLoop, reversed)
